/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/rules-of-hooks */
import React, { useState, useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { Container, Col, Row } from 'react-bootstrap';
import  {libraryManagementAction} from '../store/home/actions';
import Icons from '../themes/icons';
import Images from '../themes/images';
import '../styles/index.css';

const hackerNews = (props) => {
   const dispatch = useDispatch();
  const [pageNumber, setPageNumber] = useState(1);
  const [prevActive, setPrevActive] = useState(true);
  const [nextActive, setNextActive] = useState(false);
  const [dataPerPage, setDataPerPage] = useState(10);

  let loader = useSelector((state) => {
    return state;
  });

  useEffect(() => {
      const params = {id: 1}
      dispatch(libraryManagementAction.fetchBooksDataRequest(params));
    // eslint-disable-next-line
  }, []);

  const handleScroll = () => {
    window.scrollTo(0, 400);
  };

  const GoToPrevious = () => {
    setNextActive(false);
    setPrevActive(true);
    setPageNumber((pageNumber)=> pageNumber - 1);
    setDataPerPage(10);
  };

  const GoToNext = () => {
    setPrevActive(false);
    setNextActive(true);
    setPageNumber((pageNumber)=> pageNumber + 1);
    setDataPerPage(10);
  };


  return (
    <Container fluid>
      <Row>
        <Col lg={12}>
          <Col className="p-4 text-hn-orange items-center justify-between">
            <h1 className="text-3xl">Library Management System</h1>{' '}
          </Col>
          <Col>

          </Col>
        </Col>
      </Row>
    </Container>
  );
};

hackerNews.propTypes = {
  author: PropTypes.string,
  children: PropTypes.array,
  points: PropTypes.number,
  url: PropTypes.string,
  title: PropTypes.string,
  id: PropTypes.number,
  type: PropTypes.string,
  Comments: PropTypes.number,
  hackerNewsGraphData: PropTypes.array,
  hackerNewsData: PropTypes.array
};

export default hackerNews;
