/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/rules-of-hooks */
import React, { useState, useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { Container, Col, Row } from 'react-bootstrap';
import { libraryManagementAction } from '../store/home/actions';
import Searchbar from '../components/UiSearch/index';
import utils from '../utils/index';
import Icons from '../themes/icons';
import Images from '../themes/images';
import '../styles/index.css';

const hackerNews = (props) => {
  const dispatch = useDispatch();
  const loader = useSelector((state) => {
    return state;
  });

  const libraryData = useSelector((state) => {
    return state.libraryManagement.libraryData;
  });


  useEffect(() => {
    const params = { id: 1 }
    dispatch(libraryManagementAction.fetchBooksDataRequest(params));
    // eslint-disable-next-line
  }, []);

  const [search, setSearchState] = React.useState({
    searchText: "",
  });

  const handleScroll = () => {
    window.scrollTo(0, 400);
  };

  let { searchText } = search;

  const bookSearchHandler = (e) =>{
    console.log("@643537563758", e.target.value);
    setSearchState(e.target.value);
    !!libraryData &&
    Array.isArray(libraryData.items) &&
    libraryData.items.length > 0 &&
    libraryData.items.filter(data => 
          data.key.toString().includes(searchText)||
          data.volumeInfo.title.toUpperCase().includes(searchText) ||
          data.volumeInfo.publisher.toUpperCase().includes(searchText) 
     );
  }

  console.log("@643537563758", libraryData);

  return (
    <Container fluid>
      <Row>
        <Col lg={12}>
          <Col className="text-hn-orange items-center justify-between">
            <h1 className="text-3xl">Library Management System</h1>{' '}
            <hr className="border mtl-6"></hr>
            <Searchbar bookSearchHandler={bookSearchHandler} searchBar={searchText}   />
          </Col>
          <Col lg={12} md={12} className="p-4 inFlex parent marginBooks">
           {
              !!libraryData &&
              Array.isArray(libraryData.items) &&
              libraryData.items.length > 0 &&
              libraryData.items.map((book, index) => (
                 <Col lg={4} md={4} key={index} className="BookShell">
                      <Col lg={6} md={6} className="width50">
                        <img src={book.volumeInfo.imageLinks.thumbnail} alt=""/>
                      </Col>
                      <Col lg={6} md={6} className="width50">
                         <h4>{book.volumeInfo.title} </h4>
                         <h5>{book.volumeInfo.publisher + ' ' + book.volumeInfo.publishedDate} </h5>
                         <h6>{utils.limitText(book.volumeInfo.description, 25)}</h6>
                      </Col>
                  </Col>
              )
              )
            }
          </Col>
        </Col>
      </Row>
    </Container>
  );
};

hackerNews.propTypes = {
  author: PropTypes.string,
  children: PropTypes.array,
  points: PropTypes.number,
  url: PropTypes.string,
  title: PropTypes.string,
  id: PropTypes.number,
  type: PropTypes.string,
  Comments: PropTypes.number,
  hackerNewsGraphData: PropTypes.array,
  hackerNewsData: PropTypes.array
};

export default hackerNews;
