/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/rules-of-hooks */
import React, { useEffect } from 'react';
import { Container, Col, Row } from 'react-bootstrap';
import { Empty } from 'antd';
import Searchbar from '../components/UiSearch/index';
import utils from '../utils/index';
import '../styles/index.css';
import 'antd/dist/antd.css'

const colorCombo = (props) => {

  const [search, setSearchState] = React.useState('');
  const [color, setColor ] = React.useState(null);
  const [colorBucket, setColorBucket] = React.useState([]);

  let { searchText } = search;

  const bookSearchHandler = (e) => {
    setSearchState(e.target.value);
  }

  const generateColor = () => {
    var hexColor=[]; //new Array()
    hexColor[0] = "#"; 
    let colorList = [];
    for (var i = 0; i < search; i++) {
      for (i = 1; i < 7; i++)
      {
          var x = Math.floor((Math.random()*16)); //Tricky: Hex has 16 numbers, but 0 is one of them

          if (x >=10 && x <= 15) //hex:0123456789ABCDEF, this takes care of last 6 
          {
              switch(x)
              {
                  case 10: x="a" 
                  break;
                  case 11: x="b" 
                  break;
                  case 12: x="c" 
                  break;
                  case 13: x="d" 
                  break;
                  case 14: x="e" 
                  break;
                  case 15: x="f" 
                  break; 
                  default:
                  break; 
              }
          }
          hexColor[i] = x;
      }
      var cString = hexColor.join(""); 
      colorList.push(cString);
      setColorBucket(colorList);
    }
  };

  const selectedColor = (color) =>{
    setColor(color);
  }

  const clearAll = () =>{
    setColorBucket([]);
  }

  console.log("@878934275893475", colorBucket);

  return (
    <Container fluid>
      <Row>
        <Col lg={12}>
          <Col className="text-hn-orange items-center justify-between">
            <h1 className="text-3xl">Cogknit Task</h1>{' '}
            <hr className="border mtl-6"></hr>
          </Col>
          <Col lg={12} md={12} className="p-4 flex parent marginBooks" >
            <Col lg={6} md={6} className="width50">
              <h1 className="text-3xl">Palette</h1>{' '}
              <div className="parent flex">
                {
                  colorBucket.length> 0 ? 
                  colorBucket.map((color, index) =>(
                    <div className="colorList" onClick={()=> selectedColor(color)} style={{backgroundColor: `${color}`}}></div>
                  )):
                  (
                    <Col lg={12} md={12} className="p-4 inFlex parent marginAuto" >
                      <Empty />
                    </Col>
                  )
                }
              </div>
            </Col>
            <Col lg={6} md={6} className="width50">
              <Col lg={12} md={12}>
                <Searchbar bookSearchHandler={bookSearchHandler} searchBar={searchText} />
                <button className="btn_Button btnPreview" onClick={() => generateColor()}>Preview</button>{' '}
                <button className="btn_Button btnBorrow" onClick={() => clearAll()}>Borrow</button>{' '}
              </Col>
              <Col lg={12} md={12}>
                <h1 className="text-3xl">Selected Color</h1>{' '}
                <div className="colorPreview" style={{backgroundColor: `${color}`}}>
                </div>
              </Col>
            </Col>
          </Col>
        </Col>
      </Row>
    </Container>
  );
};


export default colorCombo;
